<?php











namespace Composer;

use Composer\Semver\VersionParser;






class InstalledVersions
{
private static $installed = array (
  'root' => 
  array (
    'pretty_version' => 'dev-master',
    'version' => 'dev-master',
    'aliases' => 
    array (
    ),
    'reference' => '2c23b9683b633c186f6ed05f4b91b9c89b22947d',
    'name' => 'mips/jeedom-worxlandroids',
  ),
  'versions' => 
  array (
    'mips/jeedom-tools' => 
    array (
      'pretty_version' => 'v1.0.1',
      'version' => '1.0.1.0',
      'aliases' => 
      array (
